INSERT [dbo].[Paragraphs] ([Id], [OrderNumber], [Header], [Text], [Lecture_Id]) VALUES (N'97ea90f7-e33d-45f3-8b63-b7e7003ce8f9', 5, N'2.2 Построение блок-схем', N'Схема — графическое представление определения, анализа или метода решения задачи, в котором используются символы для отображения операций, данных, потока, оборудования и т. д. Блок-схема — распространенный тип схем, описывающих алгоритмы или процессы, в которых отдельные шаги изображаются в виде блоков различной формы, соединенных между собой линиями.', N'85139928-5cdc-4560-8f97-44dd50f9e2d6')
