INSERT [dbo].[Achievements] ([Id], [Name], [Description], [Image], [Priority]) VALUES (N'c12e6e27-994d-4419-a0f3-f5eece73800f', N'УМНИК', N'Работа над проектом выполняется при поддержке гранта, полученного  за победу в конкурсе «Участник молодежного научно-инновационного конкурса 2012» (УМНИК) по направлению «Информационные технологии». В рамках конкурса было заслушано 59 докладов, представленных студентами и аспирантами различных вузов г. Самары.', N'umnik - diploma.jpg;umnik - letter.jpg;', 0)
INSERT [dbo].[Awards] ([Id], [Name], [Description], [Image], [Priority]) VALUES (N'6415a552-096e-4cf1-9fd3-1a52f0fcfe5b', N'АПРК - 2014', N'Всероссийская научно-техническая конференция «Актуальные проблемы радиоэлектроники и телекоммуникаций» (Самара, СГАУ, май 2014)', N'award_2_1.jpg;award_2_2.jpg;award_2_3.jpg;', 600)
INSERT [dbo].[Awards] ([Id], [Name], [Description], [Image], [Priority]) VALUES (N'ba894db4-8cab-458e-a55b-780f88abd4a1', N'АПРК - 2012', N'Всероссийская научно-техническая конференция «Актуальные проблемы радиоэлектроники и телекоммуникаций» (Самара, СГАУ, май 2014)', N'award_1_1.jpg;award_1_2.jpg;', 100)
INSERT [dbo].[Awards] ([Id], [Name], [Description], [Image], [Priority]) VALUES (N'fc3393b5-ea9a-438a-8690-b866aae30d62', N'Гагаринские чтения - 2014', N'40-ая международная молодежная научная конференция «Гагаринские чтения», посвященная 80-летию со дня рождения Юрия Гагарина (Москва, МАТИ-РГТУ имени К. Э. Циолковского, 7 – 11 апреля 2014 года)', N'belov_gagarin.jpg;petruhin_gagarin.jpg;', 400)
INSERT [dbo].[EDucationAuthors] ([Id], [Name], [Description], [Image], [Priority]) VALUES (N'8faf632e-38a9-472e-a11f-0a97d861b18b', N'Белов Константин', N'Главный разработчик системы, выпускник факультета информатики СГАУ 2014, закончил с отличием магистратуру по направлению «Фундаментальные информатика и информационные технологии». В проекте с 2011 года, разработал систему анимирования персонажей, в настоящее время занимается разработкой  сетевой версии системы (клиентская часть), провел исследования и обосновал выбор сетевых технологий и протоколов обмена данными. Является победителем проекта «У.М.Н.И.К.-2012» в команде участников. Имеет более 15 публикаций по данной тематике, выступал на Международных и Всероссийских конкурсах и конференциях, где его работы были отмечены дипломами и грамотами.', N'author_9.jpg', 300)
INSERT [dbo].[EDucationAuthors] ([Id], [Name], [Description], [Image], [Priority]) VALUES (N'1e07a5b1-baa1-4fbc-a824-165eda381732', N'Григорьев Александр', N'Разработчик веб-сайта системы, магистрант 2 года обучения факультета информатики СГАУ по направлению «Фундаментальные информатика и информационные технологии». В проекте с 2012 года, разработал подсистему авторизации пользователей, в настоящее время занимается разработкой  подсистемы администрирования, провел исследования и обосновал выбор технологий единого входа. Имеет 6 публикаций по данной тематике, выступал на Международных и Всероссийских конкурсах и конференциях, где его работы были отмечены дипломами и грамотами.', N'author_10.jpg', 600)
INSERT [dbo].[EDucationAuthors] ([Id], [Name], [Description], [Image], [Priority]) VALUES (N'c9fa665d-a35c-46f2-abe2-2276cf172ebb', N'Загуменнов Дмитрий', N'Главный архитектор системы, аспирант кафедры программных систем СГАУ, закончил факультет информатики СГАУ в 2012 году по специальности «Автоматизированные системы обработки информации и управления». В проекте с 2010 года, разработал архитектуру системы, исследовал технологии виртуальных миров, обосновал выбор технологии Unity, с помощью которой реализована система. Разработал модель данных системы и генератор виртуального обучающего пространства. Является дипломантом многих Международных и Всероссийских конкурсов и конференций, где представлял данную систему, имеет около 20 публикаций по данной тематике.', N'author_11.jpg', 100)
INSERT [dbo].[EDucationAuthors] ([Id], [Name], [Description], [Image], [Priority]) VALUES (N'5b6d81e7-c718-452d-b69b-3cff3de7686f', N'Зеленко Лариса Сергеевна', N'Руководитель проекта, доцент кафедры программных систем СГАУ, кандидат технических наук. Основатель и идейный вдохновитель проекта, занимается разработкой обучающих программ и дистанционных систем управления, а так же внедрением новых образовательных и информационных технологий в учебный процесс. Имеет более 50 публикаций по данной тематике.', N'author_1.jpg', 0)
INSERT [dbo].[EDucationAuthors] ([Id], [Name], [Description], [Image], [Priority]) VALUES (N'd7016831-5757-4863-91be-691a5c681d2d', N'Зинченко Алексей', N'Архитектор системы, выпускник факультета информатики СГАУ 2013 года, закончил с отличием магистратуру по направлению «Фундаментальные информатика и информационные технологии». В проекте с 2011 года, разработал редактор учебных курсов, участвовал в разработке модели данных, исследовал технологии разработки мобильных приложений, реализовал мобильное приложение под операционную систему  iOS. Является победителем проекта «У.М.Н.И.К.-2012» в команде участников. Имеет 13 публикаций по данной тематике, выступал на Международных и Всероссийских конкурсах и конференциях, где его работы были отмечены дипломами и грамотами.', N'author_2.jpg', 200)
INSERT [dbo].[EDucationAuthors] ([Id], [Name], [Description], [Image], [Priority]) VALUES (N'5508c2da-993c-4424-a219-740cf6d4600c', N'Иванов Виталий', N'Разработчик системы, магистрант 2 года обучения факультета информатики СГАУ по направлению «Фундаментальные информатика и информационные технологии». В проекте с 2012 года, разработал графический редактор связей между учебными материалами, доработал подсистему статистики и способ хранения материалов в системе. Имеет 10 публикаций по данной тематике, выступал на Международных и Всероссийских конкурсах и конференциях, где его работы были отмечены дипломами и грамотами.', N'author_3.jpg', 700)
INSERT [dbo].[EDucationAuthors] ([Id], [Name], [Description], [Image], [Priority]) VALUES (N'7f36676d-f898-443e-b5fb-b230c8ea1888', N'Конопелькин Дмитрий', N'Разработчик системы, магистрант 2 года обучения факультета информатики СГАУ по направлению «Фундаментальные информатика и информационные технологии». В проекте с 2012 года, занимается разработкой мобильной версии системы под операционную систему Android и администрированием системы контроля версий проекта, принимал участие в оптимизации базы данных. Имеет 12 публикаций по данной тематике, выступал на Международных и Всероссийских конкурсах и конференциях, где его работы были отмечены дипломами и грамотами.', N'author_4.jpg', 1000)
